mapper.select:
select

from x_proposal
where period=#{period}

    and  next=#{next}


    and mark=#{mark}

SELECT proid,xd.dicname protype,proany,protar,promc,prodp,prowyzh, protel,prodwjzw,proemail,protxdz,protanr,prodate,fileid, proxgqk,xd1.dicname procbdw,prolxrxm,prolxrdw,prolxrdh,joinid,protps, propj,period,NEXT,mark,postcode,xd2.dicname procategory,xd3.dicname prostate,xd4.dicname procase,prouserid, xd.dicid xd,xd1.dicid xd1,xd2.dicid xd2,xd3.dicid xd3,xd4.dicid xd4,name ,id,FY FROM x_proposal xpro LEFT JOIN x_dict xd ON xpro.protype=xd.dicid LEFT JOIN x_dict xd1 ON xpro.procbdw=xd1.dicid LEFT JOIN x_dict xd2 ON xpro.procategory=xd2.dicid LEFT JOIN x_dict xd3 ON xpro.prostate=xd3.dicid LEFT JOIN x_dict xd4 ON xpro.procase=xd4.dicid LEFT join x_user xu ON xu.id=xpro.prouserid WHERE 1=1
AND period=#{period}
AND mark=#{mark}
AND protype=#{protype}
AND procbdw=#{procbdw}
AND procategory=#{procategory}
AND prostate=#{prostate}
ORDER by prodate DESC
